    public Endereco getEndereco(){
        
        endereco.setBairro(entBairro.getText());
        endereco.setCep(entCep.getText());
        endereco.setLogradouro(entLogradouro.getText());
        endereco.setMunicipio(entMunicipio.getText());
        endereco.setNumero(entNumero.getText());
        endereco.setUf(entUf.getText());
        
        return endereco;
    }
    
    public void setEndereco(Endereco endereco){
        this.endereco = endereco;
        
        entBairro.setText(this.endereco.getBairro());
        entCep.setText(this.endereco.getCep());
        entLogradouro.setText(this.endereco.getLogradouro());
        entMunicipio.setText(this.endereco.getMunicipio());
        entNumero.setText(this.endereco.getNumero());
        entUf.setText(this.endereco.getUf());
    }
